mapper.select:
SELECT COUNT(*)
FROM LOGINMEMBER
WHERE ID=#{id}

SELECT COUNT(*)
FROM LOGINMEMBER
WHERE NICKNAME=#{nickname}

SELECT COUNT(*)
FROM LOGINMEMBER
WHERE EMAIL=#{email}

SELECT ID, NAME, NICKNAME, AGE, GENDER, EMAIL, REGI_DATE
FROM LOGINMEMBER
WHERE ID=#{id} AND PWD=#{pwd}

mapper.update:
UPDATE LOGINMEMBER
SET PWD=#{pwd}, NAME=#{name}, NICKNAME=#{nickname}, AGE=#{age}, GENDER=#{gender}, EMAIL=#{email}
WHERE ID=#{id}

mapper.select:
	SELECT ID, NAME, NICKNAME, AGE, GENDER, EMAIL, REGI_DATE
 	FROM LOGINMEMBER
 	WHERE ID=#{id}
